                  <TableCell className={`text-sm font-medium ${typeColors[event.type] || ""}`}>
                    {event.type}
                  </TableCell>
                  <TableCell className="text-sm font-mono">{event.payload}</TableCell>
                  <TableCell className="text-sm">{event.gatewayId}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </div>
      </CardContent>
    </Card>
  );
}

